mapper.select:
select p.*, l.* from product p left join likes l on pmodel=lmodel
where pbrand like concat("%",#{keyword},"%") or pename like concat("%",#{keyword},"%") or pkname like concat("%",#{keyword},"%") or pmodel like concat("%",#{keyword},"%")
limit #{pageStart},#{perPageNum}

        select p.*, u.uplike
		from product p, user u
		where pmodel=#{pmodel} and uid=#{uid}

       select p.*, u.*, l.*
		from product p, user u, likes l
		where p.pmodel=l.lmodel and p.pmodel=#{pmodel} and uid=#{uid}

mapper.update:
update product
set plcount=plcount+1
where pmodel=#{pmodel}

update product
set plcount=plcount-1
where pmodel=#{pmodel}
